from tkinter import *
import folium
import os
import webbrowser

class MapDraw(list):

    def __init__(self,
                 locations:list,
                 link_color: str = 'red',
                 link_opacity: float = 0.8,
                 link_weight:int = 3,
                 point_color:str = 'blue',
                 point_radius:int = 2,
                 point_coefficient:int = 1,
                 map_source:str = 'http://webrd02.is.autonavi.com/appmaptile?lang=zh_cn&size=1&scale=1&style=7&x={x}&y={y}&z={z}'
                 ):
        '''
        生成一个空地图在

        必要参数：\n
        locations是定位位置，格式为list，比如说[[经度, 纬度]]，例子：[[22.539254415400727, 113.95498843659973]] \n
        可选参数：\n
        link_cheak是轨迹开关，格式为str，默认是on \n
        link_color是轨迹颜色，格式为str，默认是red \n
        link_opacity是轨迹透明度，格式为float，默认是0.8 \n
        link_weight是轨迹的粗细，格式为int，默认是3 \n
        point_cheak是途经点开关，格式为str，默认是on \n
        point_color是途经点颜色，格式为str，默认是blue \n
        point_radius是途经点的大小，格式为int，默认为2 \n
        point_coefficient是途经点系数，格式为int，默认是1，打点的时候会整数这个系数，比如系数1是打印全部的点，系数2是打印一半的点 \n
        map_source是地图瓦片源，格式为str，默认为高德街景图 \n
        '''
        self.locations = locations[0::point_coefficient]
        self.link_color = link_color
        self.link_opacity = link_opacity
        self.link_weight = link_weight
        self.point_color = point_color
        self.point_radius = point_radius
        self.map_source = map_source

        # 生成一个地图
        self.m1 = folium.Map(
            location=self.locations[0],
            tiles=self.map_source,
            zoom_start=1000,
            attr='default',
            control_scale=TRUE # 地图可缩放
        )

        # 绘制起点
        folium.Marker(location=self.locations[0],
                      popup='======起点======'
                            f'经度：{self.locations[0][0]}'
                            f'纬度：{self.locations[0][1]}',
                      icon=folium.Icon(color="green")
                      ).add_to(self.m1)

        # 绘制终点
        folium.Marker(location=self.locations[-1],
                      popup='======终点======'
                            f'经度：{self.locations[-1][0]}'
                            f'纬度：{self.locations[-1][1]}',
                      icon=folium.Icon(color="red")
                      ).add_to(self.m1)

        # folium.Marker(self.locations[-1], popup='<b>终点</b>', icon=folium.Icon(color="red")).add_to(self.m1) #绘制终点

    def create_link(self):
        '''在生成的地图里，将坐标点绘制成轨迹连起来'''
        folium.PolyLine(  # polyline方法为将坐标用线段形式连接起来
            locations = self.locations,  # 将坐标点连接起来
            color = self.link_color,  # 线的颜色
            opacity = self.link_opacity, # 线的透明度
            weight = self.link_weight, # 线的大小
            popup = 'Camp Muir'
        ).add_to(self.m1)  # 将这条线添加到刚才的区域m内

    def create_point(self):
        '''在生成的地图里，将坐标点绘制成途经点标记出来'''
        for i in range(len(self.locations) - 2):
            folium.CircleMarker(  # 将坐标点绘制成可视的定位点
                location = self.locations[i + 1],   # 定位点的坐标
                popup=f'经度：{self.locations[i + 1][0]}\n 纬度：{self.locations[i + 1][1]}\n',
                color = self.point_color,   # 定位点的颜色
                radius = self.point_radius  # 定位点的大小
            ).add_to(self.m1)

    def open_map(self):
        '''将地图保存成HTML格式在目录下，并使用浏览器里打开'''
        self.m1.save(os.path.join('MAP.HTML'))
        webbrowser.open('MAP.HTML')

